int calcEdge(int x, int y, int w, int h) {
    int min = input[x][y];
    for (int i = 0; i <= w; i++) {
        if (min > input[x+i][y]) {
            min = input[x+i][y];
        }
        if (min > input[x+i][y+h]) {
            min = input[x+i][y+h];
        }
    }
    for (int i = 0; i <= h; i++) {
        if (min > input[x][y+i]) {
            min = input[x][y+i];
        }
        if (min > input[x+w][y+i]) {
            min = input[x+w][y+i];
        }
    }
    return min;
}

int calcPond(int x, int y, int w, int h, int e) {
    int sum = 0;
    for (int i = 1; i <= w-1; i++) {
        for (int j = 1; j <= h-1; j++) {
            if (input[x+i][y+j] < e) {
                sum += e - input[x+i][y+j];
            } else {
                return -1;
            }
        }
    }
    return sum;
}

