int h, w;

void solve(){
    int a[h][w];
    rep(i, h)rep(j, w) scanf("%d", &a[i][j]);
    int ans = 0;
    rep(u, h)For(d, u + 2, h)rep(l, w)For(r, l + 2, w){
        int min_h = mod;
        For(i, u, d + 1){
            chmin(min_h, a[i][l]);
            chmin(min_h, a[i][r]);
        }
        For(j, l, r + 1){
            chmin(min_h, a[u][j]);
            chmin(min_h, a[d][j]);
        }

        int tmp = 0;
        For(x, u + 1, d){
            For(y, l + 1, r){
                if(a[x][y] >= min_h){
                    tmp = -1;
                    break;
                }
                tmp += min_h - a[x][y];
            }
            if(tmp < 0) break;
        }
        chmax(ans, tmp);
    }
    printf("%d\n", ans);
}

