void solve(int d, int w){
	int a[d][w];
	for(int i=0; i<d; ++i)
		for(int j=0; j<w; ++j)
			cin >> a[i][j];
	int ans = 0;
	for(int i=0; i<d; ++i)
		for(int j=0; j<w; ++j)
			for(int k=0; k<d; ++k)
				for(int l=0; l<w; ++l){
					if(i+1 >= k || j+1 >= l) continue;
					int mi = 1e9, ma = 0;
					for(int m=i; m<=k; ++m)
						for(int n=j; n<=l; ++n){
							if(m == i || m == k || n == j || n == l) mi = min(mi, a[m][n]);
							else ma = max(ma, a[m][n]);
						}
					if(mi <= ma) continue;
					int tmp = 0;
					for(int m=i; m<=k; ++m)
						for(int n=j; n<=l; ++n)
							if(m != i && m != k && n != j && n != l)
								tmp += mi - a[m][n];
					ans = max(ans, tmp);
//					cout << i << " " << j << " " << k << " " << l << " " << tmp << " " << mi << " " << ma << "\n";
				}
	cout << ans << "\n";
}

