int main()
{
	while(scanf("%d%d",&n,&m)&&n)
	{
		ans=0;
		for(i=1;i<=n;++i)
			for(j=1;j<=m;++j)
				a[i][j]=scan();
		for(lim=1;lim<=9;++lim)
		{
			for(i=1;i<n;++i)
			for(j=1;j<m;++j)//左上
			{
				if(a[i][j]<lim||a[i+1][j]<lim||a[i][j+1]<lim)
					continue;
				for(x=i+1;x<=n;++x)
				{
					if(a[x][j+1]>=lim)
						break;
					if(a[x+1][j]<lim)
						break;
				}
				if(a[x][j+1]<lim)
					break;
				for(y=j+1;y<=m;++y)
				{
					if(a[i+1][y]>=lim)
						break;
					if(a[i][y+1]<lim)
						break;
				}
				if(a[i+1][y]<lim)
					break;
				flag=0;
				for(z=i;z<=x;++z)
					if(a[z][y]<lim)
					{
						flag=1;
						break;
					}
				for(z=j;z<=y;++z)
					if(a[x][z]<lim)
					{
						flag=1;
						break;
					}
				if(flag==1)
					continue;
				tmp=0;
				for(p=i+1;p<x&&flag==0;++p)
					for(q=j+1;q<y;++q)
					{
						if(a[p][q]>=lim)
						{
							flag=1;
							break;
						}
						tmp+=lim-a[p][q];
					}
				if(flag==0)
				{
					if(tmp>ans)
						ans=tmp;
				}
			}
		}
