int solve(int h,int w){
    vector<vector<int>> e(h,vector<int>(w));
    for(int i=0;i<h;i++) for(int j=0;j<w;j++) cin>>e[i][j];
    int res=0;
    auto calc=[&](int t,int b,int l,int r){
        int wall=10;
        for(int i=l;i<=r;i++){
            wall=min(wall,e[t][i]);
            wall=min(wall,e[b][i]);
        }
        for(int i=t;i<=b;i++){
            wall=min(wall,e[i][l]);
            wall=min(wall,e[i][r]);
        }
        int res=0;
        for(int i=t+1;i<b;i++){
            for(int j=l+1;j<r;j++){
                if(wall>e[i][j]) res+=wall-e[i][j];
                else return 0;
            }
        }
        return res;
    };
    for(int t=0;t<h;t++){
        for(int b=t+2;b<h;b++){
            for(int l=0;l<w;l++){
                for(int r=l+2;r<w;r++){
                    res=max(res,calc(t,b,l,r));
                }
            }
        }
    }
    return res;
}

