void solve(int d, int w) {
    for (int i = 0; i < d; i++) {
        for (int j = 0; j < w; j++) cin >> grid[i][j];
    }
    int ans = 0;
    for (int i = 0; i < d; i++) {
        for (int j = 0; j < w; j++) {
            for (int tate = 3; tate <= d - i; tate++) {
                for (int yoko = 3; yoko <= w - j; yoko++) {
                    int min_h = INF;
                    for (int k = i; k < i + tate; k++) {
                        min_h = min(min_h, grid[k][j]);
                        min_h = min(min_h, grid[k][j + yoko - 1]);
                    }
                    for (int k = j; k < j + yoko; k++) {
                        min_h = min(min_h, grid[i][k]);
                        min_h = min(min_h, grid[i + tate - 1][k]);
                    }
                    bool is_ok = true;
                    int now_ans = 0;
                    for (int k = i + 1; k < i + tate - 1; k++) {
                        for (int l = j + 1; l < j + yoko - 1; l++) {
                            if (min_h <= grid[k][l]) {
                                is_ok = false;
                                break;
                            }
                            now_ans += min_h - grid[k][l];
                        }
                        if (!is_ok) break;
                    }
                    if (!is_ok) continue;
                    ans = max(ans, now_ans);
                }
            }
        }
    }
