			if(x == sx || x == tx || y == sy || y == ty){
				minbd = min(minbd, a[x][y]);
			}
			else{
				maxin = max(maxin, a[x][y]);
			}
		}
	}
	if(minbd <= maxin) return 0;
	return minbd;
}

int calc(int sx, int sy, int tx, int ty, int maxbd)
{
	int ret = 0;
	for(int x = sx+1; x < tx; x++){
		for(int y = sy+1; y < ty; y++){
			ret += maxbd - a[x][y];
		}
	}
	return ret;
}

