int valid(int x,int y,int dx,int dy){
	int e = 1000000000;
	int in = 0;
	int res = 0;
	for(int i=0;i<dx;i++){
		e = min(e,f[x+i][y]);
		e = min(e,f[x+i][y+dy-1]);
	}
	for(int i=0;i<dy;i++){
		e = min(e,f[x][y+i]);
		e = min(e,f[x+dx-1][y+i]);
	}
	for(int i=1;i<dx-1;i++){
		for(int j=1;j<dy-1;j++){
			in = max(in,f[x+i][y+j]);
		}
	}
	if(in >= e)return -1;
	for(int i=1;i<dx-1;i++){
		for(int j=1;j<dy-1;j++){
			res += e-f[x+i][y+j];
		}
	}
	return res;
}
