struct Node {
    int x, y, xx, yy;
    Node() {}
    Node(int x, int y, int xx, int yy):x(x),y(y),xx(xx),yy(yy) {}
} node;
queue<Node> q;

int n, m, a[11][11];
int ans;

int main (void) {
    while (scanf("%d%d", &n, &m) && (n||m)) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                scanf("%d", &a[i][j]);
            }
        }

        ans = 0;
        memset(vis,0,sizeof(vis));

        vis[2][2][n-1][m-1] = 1;
        q.push(Node(2,2,n-1,m-1));
        while (!q.empty()) {
	        node = q.front(); q.pop();
	        if (node.x > node.xx || node.y > node.yy) continue;
	        vis[node.x][node.y][node.xx][node.yy] = 1;

	        int low = 10, up = 0, tmp = 0;
	        for (int i = node.x-1; i <= node.xx+1; i++) {
	            low = min(low,a[i][node.yy+1]);
	            low = min(low,a[i][node.y-1]);
	        }
	   	    for (int j = node.y; j <= node.yy; j++) {
	            low = min(low,a[node.x-1][j]);
	            low = min(low,a[node.xx+1][j]);
	        }
	        for (int i = node.x; i <= node.xx; i++) {
	            for (int j = node.y; j <= node.yy; j++) {
	                up = max(a[i][j],up);
	                tmp += low - a[i][j];
	            }
	        }
	        if (up < low) ans = max(tmp,ans);
	        else {
	        	if (!vis[node.x+1][node.y][node.xx][node.yy])
	          		q.push(Node(node.x+1,node.y,node.xx,node.yy));
	        	if (!vis[node.x][node.y+1][node.xx][node.yy])
	           		q.push(Node(node.x,node.y+1,node.xx,node.yy));
	        	if (!vis[node.x][node.y][node.xx-1][node.yy])
	           		q.push(Node(node.x,node.y,node.xx-1,node.yy));
	        	if (!vis[node.x][node.y][node.xx][node.yy-1])
	           		q.push(Node(node.x,node.y,node.xx,node.yy-1));
	        }
        }
        printf("%d\n", ans);
    }
    return 0;
}

