            }
        }
        for(int i = 1;i <= d;++i){
            for(int j = 1;j <= w;++j){
                for(x = 0;x < E;++x){
                    imos[x][i][j] += imos[x][i][j - 1];
                }
            }
        }
        for(int i = 1;i <= d;++i){
            for(int j = 1;j <= w;++j){
                for(x = 0;x < E;++x){
                    imos[x][i][j] += imos[x][i - 1][j];
                }
            }
        }
        for(int i = 1;i <= d;++i){
            for(int j = 1;j <= w;++j){
                for(int ii = i + 2;ii <= d;++ii){
                    for(int jj = j + 2;jj <= w;++jj){
                        // cout << i << "," << j << " " << ii << "," << jj << endl;
                        for(x = 0;x < E;++x){
                            outer[x] = imos[x][i - 1][j - 1] - imos[x][i - 1][jj] - imos[x][ii][j - 1] + imos[x][ii][jj];
                            inner[x] = imos[x][i][j] - imos[x][i][jj - 1] - imos[x][ii - 1][j] + imos[x][ii - 1][jj - 1];
                            outer[x] -= inner[x];
                            // cout << outer[x] << "," << inner[x] << endl;
                        }
                        imax = E;
                        for(x = 0;x < E;++x){
                            if(inner[x] != 0) {
                                imax = x;
                            }
                        }
                        for(x = 0;x < E;++x){
                            if(outer[x] != 0) break;
                        }
                        omin = x;
                        // cout << imax << " " << omin << endl;
                        if(imax < omin){
                            // cout << imax << "," << omin << endl;
                            int a = 0;
                            for(x = 0;x < omin;++x){
                                a += (omin - x) * inner[x];
                            }
                            ans = max(ans, a);
                        }
                    }
                }
            }
        }
        cout << ans << endl;
    }
}

