int canSpace(int sx, int sy, int gx, int gy, const vector<vector<int>>& e){
    int high = 100000;
    for (int i = sx; i <= gx; ++i) {
        high = min(high, e[i][sy]);
        high = min(high, e[i][gy]);
    }
    for (int i = sy; i <= gy; ++i) {
        high = min(high, e[sx][i]);
        high = min(high, e[gx][i]);
    }
    int space = 0;
    for (int i = sx + 1; i < gx; ++i) {
        for (int j = sy + 1; j < gy; ++j) {
            int sp = high - e[i][j];
            if(sp <= 0) return 0;
            space += sp;
        }
    }
    return space;
}

