        for (int sx = 0; sx < d; ++sx) {
            for (int sy = 0; sy < w; ++sy) {
                for (int gx = sx + 2; gx < d; ++gx) {
                    for (int gy = sy + 2; gy < w; ++gy) {
                        maxSpace = max(maxSpace, canSpace(sx, sy, gx, gy, e));
                    }
                }
            }
        }
