int C(int y1,int y2,int x1,int x2,int xx){
	for(int i=x1;i<=x2;i++){
		if(data[y1][i]==1)return 0;
		if(data[y2][i]==1)return 0;
	}
	for(int i=y1;i<=y2;i++){
		if(data[i][x1]==1)return 0;
		if(data[i][x2]==1)return 0;
	}
	int res=0;
	for(int i=y1+1;i<y2;i++){
		for(int j=x1+1;j<x2;j++){
			if(data[i][j]==0)return 0;
			res+=xx-fie[i][j];
		}
	}
	return res;
}

int solve(){
	int res=0;
	for(int i=0;i<h;i++){
		for(int j=0;j<w;j++){
			data[i][j]=1;
		}
	}
	for(int cc=9;cc>=1;cc--){
		for(int j=0;j<h;j++){
			for(int k=0;k<w;k++){
				data[j][k]=(fie[j][k]<cc);
			}
		}
		for(int i=0;i<h;i++){
			for(int j=i+2;j<h;j++){
				for(int k=0;k<w;k++){
					for(int l=k+2;l<w;l++){
						res=max(res,C(i,j,k,l,cc));
					}
				}
			}
		}
	}
	return res;
}

