int menseki (int lx, int ly, int rx, int ry) {
	int R = s[ry][rx] - s[ly - 1][rx] - s[ry][lx - 1] + s[ly - 1][lx - 1];
	return R;
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    


int solve() {
	int ans = 0;
	for (int d = 0; d <= D; d++) {
		for (int w = 0; w <= W; w++) {
			if (d * w == 0) {
				S[d][w] = 0;
				continue;
			}
			cin >> e;
			A[d][w] = e;
			S[d][w] = e + S[d - 1][w] + S[d][w - 1] - S[d - 1][w - 1];

		}
	}


	for (int v = 1; v <= 9; v++) {
		for (int d = 0; d <= D; d++) {
			for (int w = 0; w <= W; w++) {
				if (d * w == 0) {
					s[d][w] = 0;
					continue;
				}

				if (v > A[d][w]) {
					a[d][w] = 1;
					s[d][w] = 1 + s[d - 1][w] + s[d][w - 1] - s[d - 1][w - 1];
				} else {
					a[d][w] = 0;
					s[d][w] = 0 + s[d - 1][w] + s[d][w - 1] - s[d - 1][w - 1];
				}
			}
		}
