			for (int w = 0; w <= W; w++) {
				cout << s[d][w] << " ";
			}
			cout << endl;
		}
		*/
		for (int lx = 2; lx < W ; lx++) {
			for (int by = 2; by < D ; by++) {
				for (int rx = lx; rx < W ; rx ++) {
					for (int ty = by; ty < D ; ty++) {
						if (menseki(lx, by, rx, ty) == (rx - lx + 1) * (ty - by + 1) and
							menseki(lx - 1, by - 1, rx + 1, ty + 1) == (rx - lx + 1) * (ty -by + 1)) {
								ans = max(ans, v * (rx - lx + 1) * (ty - by + 1) - (S[ty][rx] - S[by - 1][rx] - S[ty][lx - 1] + S[by - 1][lx - 1]));
						}
					}
				}
			}
		}
	}
	cout << ans << endl;
}


