using namespace std;

int simulate(vector<vector<int>> &garden, int x0, int x1, int y0, int y1) 
{
    // [x0,x1) [y0,y1) に池を作った時の最大容量
    // 内側のマスが外側のマスと同じ大きさがそれより大きい、もしくは外側のマスが内側より小さい時は-1を返す

    int inner_max = -1000000, outer_min = 1000000;
    for(int i = x0; i < x1; ++i) {
        for(int j = y0; j < y1; ++j) {
            if(x0 < i && i < x1-1 && y0 < j && j < y1-1) {
                inner_max = max(inner_max, garden[i][j]);
            } else {
                outer_min = min(outer_min, garden[i][j]);
            }
        }
    }
    /*
    cout << "x0:" << x0 << " x1:" << x1 << " y0:" << y0 << " y1:" << y1 << endl;
    cout << "inner_max:" << inner_max << " outer_min:" << outer_min << endl;
    */
    if(inner_max >= outer_min) return -1;
    int res = 0;
    for(int i = x0+1; i < x1-1; ++i) {
        for(int j = y0+1; j < y1-1; ++j) {
            res += outer_min - garden[i][j];
        }
    }
    return res;
}

