int chp(int y1, int y2, int x1, int x2) {
	int res = 0, ans = 0, t = 1e9;
	for (int i = y1; i <= y2; i++) {//寻找此区域内的最大高度
		for (int j = x1; j <= x2; j++) {
			res = max(res, a[i][j]);
		}
	}

	//检查是否能够围成池塘，并寻找池塘中水平面的最高高度
	for (int i = y1 - 1; i <= y2 + 1; i++) {
		for (int j = x1 - 1; j <= x2 + 1; j++) {
			if (i == y1 - 1 || i == y2 + 1 || j == x1 - 1 || j == x2 + 1) {
				if (a[i][j] <= res)return 0;
				t = min(t, a[i][j]);
			}
		}
	}

	//计算池塘容积
	for (int i = y1; i <= y2; i++) {
		for (int j = x1; j <= x2; j++) {
			ans += t - a[i][j];
		}
	}
	return ans;
}

int ch(int y, int x) {
	int ans = 0;

	//枚举池塘
	for (int i = y; i <= h - 2; i++) {
		for (int j = x; j <= w - 2; j++) {
			ans = max(ans, chp(y, i, x, j));
		}
	}
	return ans;
}

