int garden[12][12]={0};

void init(){
    for(int i=0;i<=10;++i){
        for(int j=0;j<=10;++j)
            garden[i][j] = 0;
    }
}

int main(){
    int d, w;
    while(cin >> d >> w, d != 0){
        init();
        for(int i=0;i<d;++i){
            for(int j=0;j<w;++j){
                cin >> garden[i][j];
            }
        }

        int ans = 0;
        for(int top=0;top<d;++top){
            for(int left=0;left<w;++left){
                for(int bottom=top+2;bottom<d;++bottom){
                    for(int right=left+2;right<w;++right){
                        int mini = 11;
                        for(int i=left;i<=right;++i){
                            mini = min(mini, garden[top][i]);
                            mini = min(mini, garden[bottom][i]);
                        }
                        for(int i=top;i<=bottom;++i){
                            mini = min(mini, garden[i][left]);
                            mini = min(mini, garden[i][right]);
                        }
                        bool ok = true;
                        int sum = 0;
                        for(int i=top+1;i<bottom;++i){
                            for(int j=left+1;j<right;++j){
                                if(garden[i][j] >= mini){
                                    ok = false;
                                    break;
                                }
                                sum += mini - garden[i][j];
                            }
                            if(!ok)break;
                        }
                        if(ok)ans = max(ans, sum);
                    }
                }
            }
        }
        cout << ans << endl;
    }

    return 0;
}

