int main() {
   for(;;) {
      Int h, w;
      std::cin >> h >> w;
      if( h == 0 && w == 0 ) {
         break;
      }
      auto xss = std::vector<std::vector<Int>>(h);
      for(Int i = 0; i < h; ++i) {
         for(Int k = 0; k < w; ++k) {
            Int t;
            std::cin >> t;
            xss[i].push_back(t);
         }
      }
      Int res = 0;
      for(Int y1 = 0; y1 < h; ++y1) {
         for(Int x1 = 0; x1 < w; ++x1) {
            for(Int y2 = y1+2; y2 < h; ++y2) {
               for(Int x2 = x1+2; x2 < w; ++x2) {
                  bool valid = true;
                  Int round_min = inf;
                  for(Int y = y1; y <= y2; ++y) {
                     round_min = std::min(round_min, xss[y][x1]);
                     round_min = std::min(round_min, xss[y][x2]);
                  }
                  for(Int x = x1; x <= x2; ++x) {
                     round_min = std::min(round_min, xss[y1][x]);
                     round_min = std::min(round_min, xss[y2][x]);
                  }
                  Int t = 0;
                  for(Int y = y1+1; y <= y2-1; ++y) {
                     for(Int x = x1+1; x <= x2-1; ++x) {
                        if( xss[y][x] >= round_min ) {
                           valid = false;
                        }
                        t += round_min - xss[y][x];
                     }
                  }
                  if( valid ) {
                     res = std::max(res, t);
                  }
               }
            }
         }
      }
