fun main(args:Array<String>?): Unit {
    while(true){
        val (d, w)  = readLine()!!.trim().split(' ').map(String::toInt)
        if (d == 0 && w == 0) return
        val state = Array(d){readLine()!!.trim().split(' ').map(String::toInt).toIntArray()}
        println((2 until w).mapNotNull{right -> (0 until right - 1).mapNotNull{left -> (2 until d).mapNotNull{bottom -> (0 until bottom - 1).mapNotNull{top -> calSpace(state, left, right, top, bottom)}.max()}.max()}.max()}.max() ?: 0)
    }
}
fun calSpace(state: Array<IntArray>, left: Int, right: Int, top: Int, bottom: Int): Int? {
    val min = listOfNotNull((left .. right).map{i -> Math.min(state[top][i], state[bottom][i])}.min(), (top .. bottom).map{i -> Math.min(state[i][left], state[i][right])}.min()).min() ?: 0
    return (left + 1 until right).flatMap{x -> (top + 1 until bottom).map{y -> state[y][x]}}.let{depth ->
        if (depth.all{min > it}) depth.map{min - it}.sum()
        else null
    }
}

