import java.util.Scanner;

public class Main {

    static int makePond(int[][] g, int a, int b, int c, int d){
        int max = 0;
        for(int i=a;i<=c;i++){
            for(int j=b;j<=d;j++){
                max = Math.max(g[i][j], max);
            }
        }
        int min = 9;
        for(int j=b;j<=d;j++){
            if(!(g[a][j] > max && g[c][j] > max)) return 0;
            min = Math.min(min, Math.min(g[a][j], g[c][j]));
        }
        for(int i=a;i<=c;i++){
            if(!(g[i][b] > max && g[i][d] > max)) return 0;
            min = Math.min(min, Math.min(g[i][b], g[i][d]));
        }
        int sum = 0;
        for(int i=a;i<=c;i++){
            for(int j=b;j<=d;j++){
                sum += min - g[i][j];
            }
        }
        return sum;
    }

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        while(true){
            int d = sc.nextInt();
            int w = sc.nextInt();
            if(d + w == 0) break;
            int[][] garden = new int[d][w];
            for(int i=0;i<d;i++){
                for(int j=0;j<w;j++){
                    garden[i][j] = sc.nextInt();
                }
            }
            int max = 0;
            for(int i=1;i<d-1;i++){
                for(int j=1;j<w-1;j++){
                    for(int k=i;k<d-1;k++){
                        for(int l=j;l<w-1;l++){
                            max = Math.max(makePond(garden, i, j, k, l), max);
                        }
                    }
                }
            }
            System.out.println(max);
        }
    }
}

import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        PrintWriter out = new PrintWriter(System.out);

        int h = in.nextInt(), w = in.nextInt();
        while (h != 0) {
            int[][] b = new int[h][w];
            for (int i = 0; i < h; i++) {
                for (int j = 0; j < w; j++) {
                    b[i][j] = in.nextInt();
                }
            }

            int ans = 0;
            for (int i = 0; i < h - 2; i++) {
                for (int j = 0; j < w - 2; j++) {

                    for (int k = i + 2; k < h; k++) {
                        inner: for (int l = j + 2; l < w; l++) {
                            int min = Integer.MAX_VALUE;
                            for (int m = i; m <= k; m++) {
                                min = Math.min(min, b[m][j]);
                                min = Math.min(min, b[m][l]);
                            }
                            for (int n = j; n <= l; n++) {
                                min = Math.min(min, b[i][n]);
                                min = Math.min(min, b[k][n]);
                            }
                            //System.out.println("MIH" + min);

                            int v = 0;
                            for (int m = i + 1; m <= k - 1; m++) {
                                for (int n = j + 1; n <= l - 1; n++) {
                                    if (b[m][n] >= min) continue inner;
                                    v += min - b[m][n];
                                }
                            }
                            ans = Math.max(ans, v);
                        }
                    }
                }
            }
            out.println(ans);

            h = in.nextInt();
            w = in.nextInt();
        }
        out.flush();
    }

    private static List<String> parse(String s, boolean init) {
        List<String> res = new ArrayList<>();
        boolean q = init;
        StringBuilder sb = new StringBuilder();
        for (char c : s.toCharArray()) {
            if (c == '"') {
                if (q) {
                    res.add(sb.toString());
                    q = false;
                } else {
                    sb.setLength(0);
                    q = true;
                }
            } else if (q) {
                sb.append(c);
            }
        }
        if (q) {
            res.add(sb.toString());
        }
        return res;
    }

}

