    			for(int l = 1; l < w-1; l++) {
    				for(int r = 2; r < w; r++) {
    					for(int u = 1; u < d-1; u++) {
    						for(int b = 2; b < d; b++) {
    							min = 10;
    							if(ok(l, r, u, b)) {
    								ans = Math.max(ans, water(l, r, u, b));
    							}
    						}
    					}
    				}
    			}
    			System.out.println(ans);
    		}

    	}
    }
}

