import java.util.*;

public class Main {

    private static Scanner sc = new Scanner(System.in);

    public static void main(String[] args) {
        while (true) {
            int d = sc.nextInt();
            int w = sc.nextInt();

            if (d == 0 && w == 0)
                break;

            int[][] pond = new int[d][w];

            for (int[] row : pond) {
                for (int i=0; i < row.length; i++) {
                    row[i] = sc.nextInt();
                }
            }

            int ans = 0;

            for (int startD = 0; startD < d-2; startD++) {
                for (int startW = 0; startW < w-2; startW++) {

                    for (int endD = startD+2; endD < d; endD++) {
                        for (int endW = startW+2; endW < w; endW++) {

                            int lowestAround = check(pond, startD, startW, endD, endW);
                            if (lowestAround != -1) {

                                int size = lowestAround * (endD - startD - 1) * (endW - startW - 1)
                                        - sum(pond, startD+1, startW+1, endD-1, endW-1);
                                ans = Math.max(ans, size);

                            }

                        }
                    }

                }
            }

            System.out.println(ans);
         }

    }

    private static int check(int[][] pond, int startD, int startW, int endD, int endW) {
        int lowestAround = Integer.MAX_VALUE;
        for (int i=startD; i <= endD; i++) {
            lowestAround = Math.min(pond[i][startW], lowestAround);
            lowestAround = Math.min(pond[i][endW], lowestAround);
        }

        for (int i=startW; i <= endW; i++) {
            lowestAround = Math.min(pond[startD][i], lowestAround);
            lowestAround = Math.min(pond[endD][i], lowestAround);
        }

        for (int i=startD+1; i < endD; i++) {
            for (int j=startW+1; j < endW; j++) {
                if (pond[i][j] >= lowestAround)
                    return -1;
            }
        }

        return lowestAround;
    }

    private static int sum(int[][] pond, int startD, int startW, int endD, int endW) {
        int sum = 0;
        for (int i=startD; i <= endD; i++) {
            for (int j=startW; j <= endW; j++) {
                sum += pond[i][j];
            }
        }
        return sum;
    }

}

import java.util.*;
public class Main {

	static int max,d,w;
	public static void main(String[] args) {
		Scanner in=new Scanner(System.in);
		while(true) {
			d=in.nextInt();w=in.nextInt();
			if(d==0)break;
			int G[][]=new int[d][w]; max=0;
			for(int i=0;i<d;i++) {
				for(int j=0;j<w;j++)G[i][j]=Integer.parseInt(in.next());
			}

			for(int i=0;i<=d-3;i++) {
				for(int j=0;j<=w-3;j++)solve(G,i,j,3,3);
			}
			System.out.println(max);
		}
	}

	static void solve(int[][] A,int y,int x,int dep,int wid) {
		if(y+dep>d || x+wid>w)return;
		//int g[][]=new int[dep-y-2][wid-x-2];
		int edgmin=9,lkmax=0,maxv=0;
		for(int i=y;i<dep+y;i++) {
			for(int j=x;j<wid+x;j++) {
				if(i==y || i==dep+y-1 || j==x || j==wid+x-1)edgmin=Math.min(edgmin,A[i][j]);
				else {
					//maxv+=A[i][j];
					lkmax=Math.max(lkmax, A[i][j]);
				}
			}
		}
		if(edgmin>lkmax) {
			for(int i=y+1;i<dep+y-1;i++) {
				for(int j=x+1;j<x+wid-1;j++)maxv+=edgmin-A[i][j];
			}
		}
		//System.out.printf("y=%d x=%d dep=%d wid=%d edge=%d lake=%d sum=%d\n",y,x,dep,wid,edgmin,lkmax,maxv);
		max=Math.max(max, maxv);
		solve(A,y,x,dep+1,wid);
		solve(A,y,x,dep,wid+1);
	}

}

