public class hello
{
    public static int[,] map;
    public static int h, w;
    public static void Main()
    {
        while (true)
        {
            string[] line = Console.ReadLine().Trim().Split(' ');
            h = int.Parse(line[0]);
            w = int.Parse(line[1]);
            if (h == 0 && w == 0) break;
            map = new int[h, w];
            for (int i = 0; i < h; i++)
            {
                line = Console.ReadLine().Trim().Split(' ');
                for (int j = 0; j < w; j++)
                    map[i, j] = int.Parse(line[j]);
            }
            getRect();
        }
    }
    static int getMin(int x, int y, int x2, int y2)
    {
        var tmin = 100;
        for (int i = y; i <= y2; i++)
        {
            tmin = Min(tmin, Min(map[x, i], map[x2, i]));
            if (tmin == 0) return 0;
        }
        for (int i = x; i <= x2; i++)
        {
            tmin = Min(tmin, Min(map[i, y], map[i, y2]));
            if (tmin == 0) return 0;
        }
        return tmin;
    }
    static int getAns(int x, int y, int x2, int y2, int tmin)
    {
        var tmax = 0;
        for (int i = x + 1; i <= x2 - 1; i++)
            for (int j = y + 1; j <= y2 - 1; j++)
            {
                tmax = Max(tmax, map[i, j]);
                if (tmax >= tmin) return 0;
            }
        var ans = 0;
        for (int i = x + 1; i <= x2 - 1; i++)
            for (int j = y + 1; j <= y2 - 1; j++)
                ans += tmin - map[i, j];
        return ans;
    }
    static void getRect()
    {
        var ans = 0;
        for (int i = 0; i < h - 2; i++)
            for (int j = 0; j < w - 2; j++)
                for (int k = i + 2; k < h; k++)
                    for (int L = j + 2; L < w; L++)
                    {
                        var gmin = getMin(i, j, k, L);
                        if (gmin >= 1)
                        {
                            var res = getAns(i, j, k, L, gmin);
                            ans = Max(ans, res);
                        }
                    }
        Console.WriteLine(ans);
    }
}

