import itertools

def rdp_connect() -> bool:
    global h, w, e
    h, w = map(int, input().split())
    if h == 0 and w == 0:
        return False
    e = [list(map(int, input().split())) for _ in range(h)]
    return True

def rdp_scan(x1: int, y1: int, x2: int, y2: int) -> int:
    edges = []
    edges.extend([e[j][x1] for j in range(y1, y2 + 1)])
    edges.extend([e[j][x2] for j in range(y1, y2 + 1)])
    edges.extend([e[y1][i] for i in range(x1 + 1, x2)])
    edges.extend([e[y2][i] for i in range(x1 + 1, x2)])
    inner = [e[j][i] for j, i in itertools.product(range(y1 + 1, y2), range(x1 + 1, x2))]
    edges_min = min(edges)
    if edges_min - max(inner) <= 0:
        return 0
    return sum(map(lambda e: edges_min - e, inner))

if __name__ == '__main__':
    while rdp_connect():
        best = 0
        for y1, x1 in itertools.product(range(h), range(w)):
            for y2, x2 in itertools.product(range(y1 + 2, h), range(x1 + 2, w)):
                best = max(best, rdp_scan(x1, y1, x2, y2))
        print(best)

