        cin >> movedirection[i];
        cin >> cast[i];
        //cout << cast[i] << " " << endl;
    }
    int xPosition = 10;
    int yPosition = 10;
    int count     = 0;
    for (int i = 0; i < move; i++) {
        for (int k = 0; k < cast[i]; k++) {
            // int cast = (int)movedirection[i][1];
