                yPosition--;
            } else if (movedirection[i][0] == 'W') {
                xPosition--;
            }
            for (int j = 0; j < n; j++) {
                if (xPosition == treasurePosition[j][0] && yPosition == treasurePosition[j][1]) count++;
                // cout << xPosition << " " << yPosition << endl;
            }
        }
    }
    if (count == n)
        return "Yes";
    else
        return "No";
}

