const int dx[4] = {0, 1, 0, -1};
const int dy[4] = {1, 0, -1, 0};

int dir(char c) {
  int a = -1;
  switch(c) {
  case 'N': a=0; break;
  case 'E': a=1; break;
  case 'S': a=2; break;
  case 'W': a=3; break;
  }
  assert(a != -1);
  return a;
}

int main() {
  int N;
  while (scanf("%d", &N) != EOF && N) {
    int x, y;
    bool F[21][21];
    fill(&F[0][0], &F[0][0]+21+21, 0);
    for (int i=0; i<N; i++) {
      scanf("%d%d", &x, &y);
      F[x][y] = true;
    }
    int cx = 10;
    int cy = 10;
    int ans = 0;
    if (F[cx][cy]) {
      ans++;
      F[cx][cy] = false;
    }
    int M;
    scanf("%d", &M);
    char c;
    int l;
    for (int i=0; i<M; i++) {
      scanf(" %c%d", &c, &l); // %cの場合は改行コードも読み取ってしまう。それを防ぐために先頭に空白を入れる。
      // cerr << "c " << c << endl;
      int d = dir(c);
