using namespace std;
int* getDiff(char dir, int* diff) {
    switch (dir) {
    case 'N':
        diff[0] = 0;
        diff[1] = 1;
        return diff;
    case 'E':
        diff[0] = 1;
        diff[1] = 0;
        return diff;
    case 'S':
        diff[0] = 0;
        diff[1] = -1;
        return diff;
    case 'W':
        diff[0] = -1;
        diff[1] = 0;
        return diff;
    default:
        break;
    }
    return diff;
}
