            if (d=='N') {
                for (int j=0; j<dist; j++) {
                    field[++ry][rx]=0;
                }
            }
            if (d=='E') {
                for (int j=0; j<dist; j++) {
                    field[ry][++rx]=0;
                }
            }
            if (d=='S') {
                for (int j=0; j<dist; j++) {
                    field[--ry][rx]=0;
                }
            }
            if (d=='W') {
                for (int j=0; j<dist; j++) {
                    field[ry][--rx]=0;
                }
            }
