using namespace std;

const int MAX = 21;
int data[MAX][MAX];

pair<int,int> p;
int sum;

void N(int in, int m){
  for(int i = 0; i < in; i++){
    p.first += m;
    if(data[p.first][p.second]){
      sum--;
      data[p.first][p.second] = 0;
    }
  }
}

void E(int in, int m){
  for(int i = 0; i < in; i++){
    p.second += m;
    if(data[p.first][p.second]){
      sum--;
      data[p.first][p.second] = 0;
    }
  }
}

void move(char c, int i){
  if(c == 'N') N(i,1);
  if(c == 'S') N(i,-1);
  if(c == 'E') E(i,1);
  if(c == 'W') E(i,-1);
}

