				: (d == 'W') ? -1
				: 0;
			const int addY = (d == 'N') ? 1
				: (d == 'S') ? -1
				: 0;
			for (int i = 0; i < l; ++i) {
				f[robotY][robotX] = false;
				robotX += addX;
				robotY += addY;
			}
			f[robotY][robotX] = false;
		}

		// ツ陛ウツ静篠つェツ残ツづ?づ?つ「ツづ按つ「ツつゥツ陳イツづ猟づゥ
		bool exist = false;
		for (int y = 0; y < HEIGHT; ++y) {
			for (int x = 0; x < WIDTH; ++x) {
				if (f[y][x]) {
					exist = true;
				}
			}
		}

		// ツ出ツ療?
		std::cout << ((!exist) ? "Yes" : "No") << std::endl;
	}
	return 0;
}

