const Int MOD = 1000000007;

Int mod_pow(Int x, Int n) {
    Int res = 1;
    while(n > 0) {
        if(n & 1) res = (res * x) % MOD; //ビット演算(最下位ビットが1のとき)
        x = (x * x) % MOD;
        n >>= 1; //右シフト(n = n >> 1)
    }
    return res;
}

//最大公約数
template<typename T>
T gcd(T a, T b) {
    return b != 0 ? gcd(b, a % b) : a;
}

//最小公倍数
template<typename T>
T lcm(T a, T b) {
    return a * b / gcd(a, b);
}

bool check(int x, int y, vector<pair<int, int>> &jewelry) {
    for (auto &i: jewelry) {
        if (i.first == x && i.second == y) {
            i.first = -1;
            i.second = -1;
            return true;
        }
    }
    return false;
}

string solve(int N) {
    vector<pair<int, int>> jewelry;
    for (int i = 0; i < N; i++) {
        int x, y; cin >> x >> y;
        jewelry.push_back({x, y});        
    }
    int x, y;
    x = y = 10;

    int M; cin >> M;
    size_t cnt = 0;
    for (int i = 0; i < M; i++) {
        char dir;
        int dist;
        cin >> dir >> dist;
        if (dir == 'N') {
            for (int j = 0; j < dist; j++) {
                y++;
                if (check(x, y, jewelry)) cnt++;
            }
        } else if (dir == 'S') {
            for (int j = 0; j < dist; j++) {
                y--;
                if (check(x, y, jewelry)) cnt++;
            }            
        } else if (dir == 'E') {
            for (int j = 0; j < dist; j++) {
                x++;
                if (check(x, y, jewelry)) cnt++;
            }            
        } else if (dir == 'W') {
            for (int j = 0; j < dist; j++) {
                x--;
                if (check(x, y, jewelry)) cnt++;
            }            
        }    
    }
    if (cnt == jewelry.size()) return "Yes";
    else return "No";
}

int main(){
    cin.tie(0);
    ios::sync_with_stdio(false);

    vector<string> ans;
    while (1) {
        int N; cin >> N;
        if (!N) break;
        ans.push_back(solve(N));
    }

    for (auto &i: ans) {
        cout << i << endl;
    }
}

