void init_map(){
	now_x=now_y=10;
	n=0;
	for(int i=0; i<21; i++)
		for(int j=0; j<21; j++)
			map[i][j]=0;
}

void go(char dir, int dis){
	int d;
	if(dir=='N')d=0;
	else if(dir=='E')d=1;
	else if(dir=='S')d=2;
	else if(dir=='W')d=3;

	for(int i=0; i<dis; i++){
		now_x+=vector[d][0];
		now_y+=vector[d][1];
		if(map[now_y][now_x]==1){
			n++;
			map[now_y][now_x]=0;
		}
	}
}

int main(){
	int N, M;
	while(true){
		scanf("%d", &N);
		if(N==0)break;
		init_map();
		for(int i=0; i<N; i++){
			int x, y;
			scanf("%d %d", &x, &y);
			map[y][x]=1;
		}
		scanf("%d", &M);
		for(int i=0; i<M; i++){
			char dir;
			int dis;
			scanf(" %c %d", &dir, &dis);
			go(dir, dis);
		}
		if(N==n) puts("YES");
		else puts("NO");
	}
	return 0;
}

