struct ans{
	int n;
	ans *next;
};

int main(){
	ans *start=new ans();
	start->next=NULL;
	ans *end=start,*buf;
	char s[20];
	char dir;
	char *ps;
	int **juel;
	int x=10,y=10;
	int state[20][20];
	int i,j,n,m,size;
	while(true){
		fgets(s,sizeof(s),stdin);
		size=atoi(s);
		if(size==0) break;
		memset(state,0,sizeof(state));
		juel=new int*[size];
		for(i=0;i<size;i++){
			juel[i]=new int[3];
			fgets(s,sizeof(s),stdin);
			ps=strtok(s," ");
			juel[i][0]=atoi(ps);
			ps=strtok(NULL," ");
			juel[i][1]=atoi(ps);
			juel[i][2]=1;
		}
		fgets(s,sizeof(s),stdin);
		n=atoi(s);
		for(i=0;i<n;i++){
			fgets(s,sizeof(s),stdin);
			ps=strtok(s," ");
			dir=ps[0];
			ps=strtok(NULL," ");
			m=atoi(ps);
			switch(dir){
			case 'N':
				for(j=1;j<=m;j++) state[x][y+j]=1;
				y+=m;
				break;
			case 'S':
				for(j=1;j<=m;j++) state[x][y-j]=1;
				y-=m;
				break;
			case 'E':
				for(j=1;j<=m;j++) state[x+j][y]=1;
				x+=m;
				break;
			case 'W':
				for(j=1;j<=m;j++) state[x-j][y]=1;
				x-=m;
				break;
			}
		}
		for(i=0;i<size;i++)if(state[juel[i][0]][juel[i][1]]==0) break;
		if(i==size) n=0;
		else n=1;
		buf=new ans();
		buf->n=n;
		buf->next=NULL;
		end->next=buf;
		end=buf;
	}
	buf=start->next;
	while(buf){
		if(buf->n==0) cout<<"Yes"<<endl;
		else cout<<"No"<<endl;
		end=buf;
		buf=buf->next;
		delete end;
	}
	for(i=0;i<size;i++) delete []juel[i];
	delete []juel;
	return 1;
}

