bool solve() {
    int n;
    cin >> n;
    if(n == 0) {
        return false;
    }
    set<P> se;
    for(int i = 0; i < n; i++) {
        P tmp;
        cin >> tmp.first >> tmp.second;
        se.insert(tmp);
    }
    P now = P(10, 10);
    int m, ans = 0;
    cin >> m;
    for(int _ = 0; _ < m; _++) {
        char d;
        int l;
        cin >> d >> l;
        // cout << d << " " << l << endl;
        P dir;
        if(d == 'N') {
            dir = P(0, 1);
        } else if(d == 'S') {
            dir = P(0, -1);
        } else if(d == 'E') {
            dir = P(1, 0);
        } else {
            dir = P(-1, 0);
        }
        // cout << dir.first << " " << dir.second << endl;
        for(int i = 0; i < l; i++) {
            now.first += dir.first;
            now.second += dir.second;
            // cout << now.first << " " << now.second << endl;
            if(se.count(now)) {
                ans++;
                se.erase(now);
            }
        }
    }
    cout << (n == ans ? "Yes" : "No") << endl;
    return 1;
}

