    rep(i,m){
      for(j=1;j<=move2[i];j++){
	if(move1[i] == 'N'){
	  mapQ[++starty][startx];
	  if(mapQ[starty][startx] == 1)count++;
	  mapQ[starty][startx]=0;
	}
	else if(move1[i] == 'E'){
	  mapQ[starty][++startx];
	  if(mapQ[starty][startx] == 1)count++;
	 mapQ[starty][startx]=0;
	}
	else if(move1[i] == 'S'){
	  mapQ[starty][--startx];
	  if(mapQ[starty][startx] == 1)count++;
	 mapQ[starty][startx]=0;
	}
	else if(move1[i] == 'W'){
	  mapQ[--starty][startx];
