    //   cout << "count =" << count <<  " n =" << n << endl;    


    count == n ? cout << "Yes" << endl : cout << "No" << endl ; 


  }


 return 0;

}

#include <bits/stdc++.h>

using namespace std;

bool solve(){
    int n;
    cin>>n;
    if(n==0)return false;
    int tx=10,ty=10;
    vector<pair<int,int>> g(n);
    for (int i = 0; i < n; ++i) {
        int a,b;
        cin>>a>>b;
        g[i]=make_pair(a,b);
    }
    int m;
    cin>>m;
    vector<bool> totta(n,false);
    for (int i = 0; i < m; ++i) {
        char c;
        cin>>c;
        int d;
        cin>>d;
        for (int k = 0; k < d; ++k) {
            if(c=='N'){
                ty++;
            }
            else if(c=='E'){
                tx++;
            }
            else if(c=='S'){
                ty--;
            }
            else if(c=='W'){
                tx--;
            }
            else{
                return false;
            }
            for (int j = 0; j < n; ++j) {
                if(g[j].first==tx&&g[j].second==ty)totta[j]=true;
            }
        }
    }
    for(auto i:totta){
        if(!i){
            cout<<"No"<<endl;
            return true;
        }
    }
    cout<<"Yes"<<endl;
    return true;
}

int main(){
    while(solve());
    return 0;
}

