	while (cin >> N)
	{
		if (N == 0) break;

		//gridの初期化
		for (int y=0; y<21; y++)
		{
			for (int x=0; x<21; x++)
			{
				grid[y][x] = '.';
			}
		}

		//宝石の位置の入力
		for (int i=0; i<N; i++)
		{
			cin >> in_x >> in_y;
			grid[in_y][in_x] = '#';
		}

		cin >> M;
		now_x = now_y = 10;
		for (int i=0; i<M; i++)
		{
			cin >> d >> l;
			for (int j=1; j<=l; j++)
			{
				if (d == 'N')
				{
					grid[now_y + j][now_x] = '.';
					if (j == l)
					{
						now_y += j;
					}
				} else if (d == 'E') {
					grid[now_y][now_x + j] = '.';
					if (j == l)
					{
						now_x += j;
					}
				} else if (d == 'S') {
					grid[now_y - j][now_x] = '.';
					if (j == l)
					{
						now_y -= j;
					}
				} else if (d == 'W') {
					grid[now_y][now_x - j] = '.';
					if (j == l)
					{
						now_x -= j;
					}
				}
