        int set_jem_x, set_jem_y;
        std::cin >> set_jem_x;
        std::cin >> set_jem_y;
        jems_matrix[set_jem_x][set_jem_y] = 1;
    }
}

class Robot
{
public:
    explicit Robot()
        : m_x(10), m_y(10) {};
    void getJem();
    void move(char, int);
private:
    int m_x;
    int m_y;
};

void Robot::getJem()
{
    if (jems_matrix[m_x][m_y] == 1) {
        jems_matrix[m_x][m_y] = 0;
        jems_num--;
    }
}

void Robot::move(char dir, int dist)
{

    for (int i = 0; i < dist; i++) {
        if (dir == 'N') {
            m_y++;
        }
        if (dir == 'S') {
            m_y--;
        }
        if (dir == 'E') {
            m_x++;
        }
        if (dir == 'W') {
            m_y--;
        }

        getJem();
    }
}


