class Point {
public:
  int x, y;
  Point(int x, int y) : x(x), y(y){};
};

int main() {
  while (1) {
    int n;
    cin >> n;
    if (n == 0)
      break;

    vector<Point> gold;
    int x, y;
    for (int i = 0; i < n; i++) {
      cin >> x >> y;
      gold.push_back(Point(x, y));
    }

    int m;
    cin >> m;
    char dir;
    int dist;
    int isVisited[22][22] = {};
    Point cur(10, 10);
    isVisited[10][10] = 1;
    for (int i = 0; i < m; i++) {
      cin >> dir >> dist;
      switch (dir) {
      case 'N':
        for (int j = 0; j < dist; j++) {
          isVisited[cur.x][++cur.y] = 1;
        }
        break;

      case 'S':
        for (int j = 0; j < dist; j++) {
          isVisited[cur.x][--cur.y] = 1;
        }
        break;

      case 'E':
        for (int j = 0; j < dist; j++) {
          isVisited[++cur.x][cur.y] = 1;
        }
        break;

      case 'W':
        for (int j = 0; j < dist; j++) {
          isVisited[--cur.x][cur.y] = 1;
        }
        break;
      }
    }

    int k;
    for (k = 0; k < gold.size(); k++) {
      if (isVisited[gold[k].x][gold[k].y] == 0) {
        break;
      }
    }

    if (k == gold.size()) {
      cout << "Yes" << endl;
    } else {
      cout << "No" << endl;
    }
  }

  return 0;
}

