int x[20], y[20];

int check(int nx, int ny, int N) {
    int cnt = 0;
    for(int i = 0; i < N; i++) {
        if(nx == x[i] && ny == y[i]) {
            cnt++;
            x[i] = y[i] = -1;
        }
    }
    return cnt;
} 

int main() {
    int N, M, l;
    char d;

    while(1) {
        cin >> N;
        if(N == 0) break;
        for(int i = 0; i < N; i++) {
            cin >> x[i] >> y[i];
        }
        cin >> M;
        int nx = 10, ny = 10, cnt = 0;
        for(int i = 0; i < M; i++) {
            cin >> d >> l;
            if(d == 'N') {
                for(int j = 0; j < l; j++) {
                    ny++;
                    cnt += check(nx, ny, N);
                }
            }
            else if(d == 'S') {
                for(int j = 0; j < l; j++) {
                    ny--;
                    cnt += check(nx, ny, N);
                }
            }
            else if(d == 'E') {
                for(int j = 0; j < l; j++) {
                    nx++;
                    cnt += check(nx, ny, N);
                }
            }
            else {
                for(int j = 0; j < l; j++) {
                    nx--;
                    cnt += check(nx, ny, N);
                }
            }
        }
        if(cnt == N) {
            cout << "Yes" << endl;
        }
        else {
            cout << "No" << endl;
        }
    }
