void calc(vector<pair<int,int>> &v, char c, int d) {
  int x = X, y = Y;
  switch (c) {
    case 'N':
      Y += d;
      break;
    case 'W':
      X -= d;
      break;
    case 'S':
      Y -= d;
      break;
    case 'E':
      X += d;
      break;
  }
  for (int i = 0; i < v.size(); i++) {
    if ((x <= v[i].first && v[i].first <= X) || (X <= v[i].first && v[i].first <= x)) {
      if ((y <= v[i].second && v[i].second <= Y) || (Y <= v[i].second && v[i].second <= y)) {
        v.erase(v.begin() + i);
        i--;
      }
    } 
  }
}

