        for(int i=0; i<m; i++){
            char dir; int masu;

            cin>>dir>>masu;

            if(dir == 'N'){
                for(int j=y; j<=y+masu; j++){
                    check[x][j] = true;
                }
                y+=masu;
            }

            else if(dir == 'S'){ 
                for(int j=y; j>=y-masu; j--){
                    check[x][j] = true;
                }
                y -= masu;
            }

            else if(dir == 'W'){
                for(int j=x; j>=x-masu; j--){
                    check[j][y] = true;
                }
                x -= masu;
            }

            else if(dir == 'E'){
                for(int j=x; j<=x+masu; j++){
                    check[j][y] = true;
                }
                x += masu;
            }
        }

        for(int j=0; j<area.size(); j++){
            int ax = area[j].first,ay=area[j].second;

            if(check[ax][ay]) count++;
        }

        if(count == n) cout<<"Yes"<<endl;

        else cout<<"No"<<endl;

    }

    return 0;
