  for (size_t i = 0; i < N; ++i) {
    size_t x, y;
    scanf("%zu %zu", &x, &y);
    a[y][x] = 1;
  }

  size_t M;
  scanf("%zu", &M);
  size_t x = 10;
  size_t y = 10;
  a[y][x] = 0;
  for (size_t i = 0; i < M; ++i) {
    char d;
    size_t w;
    scanf(" %c %zu", &d, &w);
    if (d == 'N') {
      for (size_t j = 0; j < w; ++j) {
        a[++y][x] = 0;
      }
    } else if (d == 'E') {
      for (size_t j = 0; j < w; ++j) {
        a[y][++x] = 0;
      }
    } else if (d == 'S') {
      for (size_t j = 0; j < w; ++j) {
        a[--y][x] = 0;
      }
    } else if (d == 'W') {
      for (size_t j = 0; j < w; ++j) {
        a[y][--x] = 0;
      }
    }
  }

  int res = 0;
  for (auto const& ai: a) res += std::accumulate(ai.begin(), ai.end(), 0);
  puts(!res? "Yes": "No");
  return 0;
}

