#define MAX 32

using namespace std;

int main(void) {
	while(1) {
		int n, m;
		int x[MAX], y[MAX];
		int m_x = 10, m_y = 10;
		char d;
		int l;
		bool moved[MAX][MAX] = {false};
		moved[10][10] = true;
		bool collect = true;

		scanf("%d", &n);
		if(n == 0) break;
		for(int i = 0; i < n; i++) {
			scanf("%d %d", &x[i], &y[i]);
		}
		scanf("%d", &m);
		for(int i = 0; i < m; i++) {
			cin >> d >> l;
			if(d == 'N') {
				for(int j = 0; j <= l; j++) {
					moved[m_x][m_y + j] = true;
				}
				m_y += l;
			}
			if(d == 'E') {
				for(int j = 0; j <= l; j++) {
					moved[m_x + j][m_y] = true;
				}
				m_x += l;
			}
			if(d == 'S') {
				for(int j = 0; j <= l; j++) {
					moved[m_x][m_y - j] = true;
				}
				m_y -= l;
			}
			if(d == 'W') {
				for(int j = 0; j <= l; j++) {
					moved[m_x - j][m_y] = true;
				}
				m_y -= l;
			}
		}

		for(int i = 0; i < n; i++) {
			if(!moved[x[i]][y[i]]) collect = false;
		}

		printf("%s\n", collect ? "Yes" : "No");
	}
	return 0;
}

