void retrieve(vector<pos>& gems, mov way, pos& robot)
{
  int change, stay;
  pos temp;
  char dir = way.direction;
  if(dir=='N'){
    change = robot.y;
    stay = robot.x;
    while(change < robot.y+way.distance){
      ++change;
      temp.x = stay;
      temp.y = change;
      auto itr = find(gems.begin(), gems.end(), temp);
      if(itr != gems.end()){
	gems.erase(itr);
      }
    }
    robot.y += way.distance;
  }
  else if(dir=='E'){
    change = robot.x;
    stay = robot.y;
    while(change < robot.x+way.distance){
      ++change;
      temp.y = stay;
      temp.x = change;
      auto itr = find(gems.begin(), gems.end(), temp);
      if(itr != gems.end()){
	gems.erase(itr);
      }
    }
    robot.x += way.distance;
  }
  else if(dir=='S'){
    change = robot.y;
    stay = robot.x;
    while(change > robot.x-way.distance){
      --change;
      temp.x = stay;
      temp.y = change;
      auto itr = find(gems.begin(), gems.end(), temp);
      if(itr != gems.end()){
	gems.erase(itr);
      }
    }
    robot.y -= way.distance;
  }
  else if(dir=='W'){
    change = robot.x;
    stay = robot.y;
    while(change > robot.x-way.distance){
      --change;
      temp.y = stay;
      temp.x = change;
      auto itr = find(gems.begin(), gems.end(), temp);
      if(itr != gems.end()){
	gems.erase(itr);
      }
    }
    robot.x -= way.distance;
  }
}

