#include <bits/stdc++.h>
#include <sys/time.h>
using namespace std;

double myclock()
{
	timeval t; gettimeofday(&t,0);
	return t.tv_sec+t.tv_usec*1e-6;
}

inline int my_popcount(unsigned x)
{
	x=(x&0x55555555)+(x>>1&0x55555555);
	x=(x&0x33333333)+(x>>2&0x33333333);
	x=(x&0x0f0f0f0f)+(x>>4&0x0f0f0f0f);
	x=(x&0x00ff00ff)+(x>>8&0x00ff00ff);
	return (x&0x0000ffff)+(x>>16&0x0000ffff);
}

int main()
{
	constexpr int LOOP=100000000;

	int sum1=0,sum2=0; // ループが削除されてしまわないようにする

	double t0=myclock();

	for(int i=0;i<LOOP;i++)
		sum1+=__builtin_popcount(i);

	double t1=myclock();

	for(int i=0;i<LOOP;i++)
		sum2+=my_popcount(i);

	double t2=myclock();

	printf("sum1=%d sum2=%d\n",sum1,sum2);
	printf("__builtin_popcount: %f[s]\n",t1-t0);
	printf("my_popcount:        %f[s]\n",t2-t1);
}

