int move(char direction, int dis) {
    int tmp = 0;
    rep(i, dis) {
        if (direction=='N') cur.second++;
        if (direction=='S') cur.second--;
        if (direction=='E') cur.first++;
        if (direction=='W') cur.first--;
        if (mat[cur.first][cur.second]=='1') {
            tmp++;
            mat[cur.first][cur.second]='0';
        }
    }
    return tmp;
}

