#include <bits/stdc++.h>
using namespace std;

int main(){
	int robo[2];
	int n,m,h;//nは宝石の数
	char com;
	refrain:
	scanf("%d",&n);
	if(n==0) return 0;
	robo[0]=10;	 robo[1]=10;//ロボットの初期位置(x,y)
	int houseki[n][2],tn=n;//tn==0でクリア
	for(int i=0;i<n;i++){
		scanf("%d %d",&houseki[i][0],&houseki[i][1]);//宝石の位置(x,y)
	}
	scanf("%d",&m);
	for(int i=0;i<m;i++){
		scanf(" %c %d",&com,&h);
		if(com=='N'){//y軸方向+の移動
			for(int j=0;j<n;j++){
				if(houseki[j][0]==robo[0]){
					if(robo[1]<houseki[j][1]&&robo[1]+h>=houseki[j][1]){//通った判定
						tn--;
						houseki[j][0]=-1;
						houseki[j][1]=-1;
					}
				}
			}
			robo[1]+=h;
		}
		else if(com=='S'){//y軸方向-の移動
			for(int j=0;j<n;j++){
				if(houseki[j][0]==robo[0]){
					if(robo[1]>houseki[j][1]&&robo[1]-h<=houseki[j][1]){//通った判定
						tn--;
						houseki[j][0]=-1;
						houseki[j][1]=-1;
					}
				}
			}
			robo[1]-=h;
		}	
		else if(com=='E'){//x軸方向+の移動
			for(int j=0;j<n;j++){
				if(houseki[j][1]==robo[1]){
					if(robo[0]<houseki[j][0]&&robo[0]+h>=houseki[j][0]){//通った判定
						tn--;
						houseki[j][0]=-1;
						houseki[j][1]=-1;
					}
				}
			}
			robo[0]+=h;
		}
		else if(com=='W'){//x軸方向-の移動
			for(int j=0;j<n;j++){
				if(houseki[j][1]==robo[1]){
					if(robo[0]>houseki[j][0]&&robo[0]-h<=houseki[j][0]){//通った判定
						tn--;
						houseki[j][0]=-1;
						houseki[j][1]=-1;
					}
				}
			}
			robo[0]+=h;
		}
		if(tn<=0){
			printf("Yes\n");
			goto refrain;
		}
	}
	printf("No\n");
	goto refrain;
}

